mapper.update:
UPDATE MEMBERS
SET MSTATE = #{mstate}
WHERE MID = #{mid}

UPDATE CAMPING
SET CASTATE = #{castate}
WHERE CACODE = #{cacode}

UPDATE CAMPING
CANAME = #{caname}, CALINECONTENTS = #{calinecontents}, CACONTENTS = #{cacontents}, CAINFO = #{cainfo}, CATYPE = #{catype},
CAIMAGE = #{caimage}
WHERE CACODE = #{cacode}

UPDATE BANNER
BNIMAGE = #{bnimage}, BNTITLE = #{bntitle}, BNTITLEEFFECT = #{bntitleeffect}, BNCONTENTS = #{bncontents}, BNCONTENTSEFFECT = #{bncontentseffect}, BNLINK = #{bnlink}, BNLINKEFFECT = #{bnlinkeffect}
WHERE BNCODE = #{bncode}

UPDATE BOARDS
SET BOSTATE = #{bostate}
WHERE BOCODE = #{bocode}

UPDATE GOODSREVIEW
SET GORVSTATE = #{gorvstate}
WHERE GORVCODE = #{gorvcode}

UPDATE CAMPINGREVIEW
SET CGRVSTATE = #{cgrvstate}
WHERE CGRVCODE = #{cgrvcode}
